import React, { useEffect, useState } from "react";
import API_Details from "../API_Details";
import Carousel from "../Components/HomePage/Carousel/Carousel";
import Hero from "../Components/HomePage/Hero/Hero";

export type MovieState = {
  adult: boolean;
  backdrop_path: string;
  genre_ids: number[];
  id: number;
  original_language: string;
  original_title: string;
  overview: string;
  popularity: number;
  poster_path: string;
  release_date: string;
  title?: string;
  name?: string;
  video: boolean;
  vote_average: number;
  vote_count: number;
};

const Home = () => {
  const [movies, setMovies] = useState<MovieState[]>([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = (): void => {
    fetch(API_Details.POPULAR_MOVIES_URL)
      .then((res) => res.json())
      .then((data) => setMovies(data.results))
      .catch((err) => console.log(err));
  };
  console.log(movies.length);

  return (
    <div>
      {movies.length !== 0 && (
        <Hero
          image1={`${API_Details.BASE_IMAGE_URL}${API_Details.BACKDROP_SIZE}${movies[0].backdrop_path}`}
          image2={`${API_Details.BASE_IMAGE_URL}${API_Details.BACKDROP_SIZE}${movies[0].backdrop_path}`}
          image3={`${API_Details.BASE_IMAGE_URL}${API_Details.BACKDROP_SIZE}${movies[0].backdrop_path}`}
        />
      )}
      <Carousel
        movieList={movies}
        carouselTitle="Popular Movies"
        autoplay={true}
      />
    </div>
  );
};

export default Home;
