import React from "react";
import {
  StyledNavHeader,
  StyledNavContent,
  StyledLogo,
  StyledLinks,
  StyledLinkItems,
  StyledSignIn,
  SignOutButton,
  StyledSignOut,
  Username,
} from "./Navbar.styled";
import { Container } from "../../../Styles/CommonStyles/Container";

import { Link } from "react-router-dom";

type UserState = {
  email: string;
  username: string;
  password: string;
  favoriteMovies: number[];
};

const Navbar = () => {
  const user = JSON.parse(window.localStorage.getItem("user")!);

  // const dispatch = useDispatch();
  const handleSignOut = () => {
    const updatedUser = JSON.parse(window.localStorage.getItem("user")!);
    const allUsers = JSON.parse(window.localStorage.getItem("users")!);

    const updatedUserArray = allUsers.filter(
      (item) => item.username !== user.username
    );

    updatedUserArray.push(updatedUser);

    window.localStorage.setItem("users", JSON.stringify(updatedUserArray));

    window.localStorage.setItem("user", JSON.stringify(null));

    // dispatch(logout());

    window.location.reload();
  };
  return (
    <StyledNavHeader>
      <Container>
        <StyledNavContent>
          <StyledLogo>MovieNow</StyledLogo>
          <StyledLinks>
            <Link to="/">
              <StyledLinkItems>Home</StyledLinkItems>
            </Link>

            <Link to="/movies">
              <StyledLinkItems>Movies</StyledLinkItems>
            </Link>
          </StyledLinks>
        </StyledNavContent>
      </Container>
    </StyledNavHeader>
  );
};

export default Navbar;
